    const [formData, setFormData] = useState({
        email: "",
        name: "",
        phone: "",
        department: ""
    });

    const navigate = useNavigate();

    const handleInputChange = (event) => {
        const { name, value } = event.target;
        console.log(name, value); // Add this line for debugging
